            </div>
            <div className="board-row">
                <Square value={squares[6]} onSquareClick={handleClick} />
                <Square value={squares[7]} onSquareClick={handleClick} />
                <Square value={squares[8]} onSquareClick={handleClick} />
            </div>

        </>
    );
}
